import React, {useContext} from 'react'
import {useHistory} from 'react-router-dom'
import {GlobalContext} from "../App"
import {connect} from "react-redux";
import {SET_PERSON_BY_ID} from "../../store/typesList";

import Albums from './Albums';

const AlbumCard = ({album, getPersonById}) => {
    // const {getPersonById} = useContext(GlobalContext)
    const person = null

    useEffect(() => {
         person = getPersonById(album.personId)
    }, []);


    let history = useHistory()

    const clickHandler = event => {
        event.preventDefault()
        history.push(`/albums/${album.id}`)
    }

    return (
        <div className="col-6 col-sm-4 col-md-3">
            <div className="card cur-pointer" onClick={clickHandler}>
                {/* <img src={photo.src} alt={album.title}/> */}
                <div className="card-body">
                    <h3 className="card-title">{album.title}</h3>
                    <p className="card-text">{person.lName} {person.fName[0].toUpperCase()}.</p>
                </div>
            </div>
        </div>
    )
}

// const mapStateToProps = state => {
//     albums: state.albums.list
// }

const mapDispatchToProps = dispatch => {
    return {
        getPersonById: (id) => dispatch({type: SET_PERSON_BY_ID, payload:id}),
        // addLocalPost: post => dispatch(addPost(post))
    }
}

export default connect(null, mapDispatchToProps)(AlbumCard)
// export default AlbumCard
