            return (
                <h1>No albums</h1>
            )
        }
        return (
            <div className="row">
                {albums.map((album) => {
                    const albumPhotos = photos.filter(p=>p.albumId === album.id)
                    if ( albumPhotos.length === 0 ) return null
                    return (<AlbumCard key={album.id} album={album} photo={albumPhotos[0]}/>)
                })}
            </div>
        )
    }

    return (
        <div className="container">
            {renderAlbums()}
