import albumsInitial, {setAlbumsToStorage} from '../../data/albums'
import {ADD_ALBUM, CHANGE_ADD_ALBUM, FETCH_ALBUM, SET_ALBUM_BY_ID} from "../typesList";
// import personsInitial, {setPersonsToStorage} from "../../data/persons";


export const getAlbums = () =>{
    return async dispatch => {
        try {
            const obj = getObj()
            await dispatch(fetchAlbums(obj))
        } catch (e) {
            console.log(e.message)
        }
    }
}

export const addAlbum = album => {
    return async dispatch => {
        try {
            const newAlbum = await addAlbumInServer(album)
            await dispatch(addPostInState(newAlbum))
        } catch (e) {
            console.log(e.message)
        }
    }
}

const fetchAlbums = data => {
    return {
        type: FETCH_ALBUM,
        payload: data
    }
}

const addPostInState = albums => {
    return {
        type: ADD_ALBUM,
        payload: albums
    }
}

export const setAlbumById = albumId => {
    return dispatch => {
        try {
            dispatch( setAlbumByIdInState(albumId) )
        } catch (e) {
            console.log(e.message)
        }
    }
}

const setAlbumByIdInState = albumId => {
    return {
        type: SET_ALBUM_BY_ID,
        payload: albumId
    }
}

//Server Side

const getObj = () => {
    return {
        list: [...albumsInitial]
    }
}

const addAlbumInServer = album => {
    const newAlbum = {
        ...album,
        id: Date.now()
    }
    albumsInitial.push(newAlbum)
    setAlbumsToStorage(albumsInitial)
    return newAlbum
}
